<?php

namespace MyCp\mycpBundle\Entity;
use Doctrine\ORM\EntityRepository;

/**
 * availabilityOwnerRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class taskRentaRepository extends EntityRepository
{
    function getCanceledBooking() {
        $qb = $this->createQueryBuilder('a');

        $qb->where('a.type = :type')->setParameter('type', taskRenta::TYPE_CANCELED_BOOKING)
            ->andWhere('a.status = :status')->setParameter('status', taskRenta::STATUS_PENDING);

        return $qb->getQuery()->execute();
    }
}
